    private void btnSalBrutoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSalBrutoActionPerformed
        // TODO add your handling code here:
        
        double Horas_Trabajadas = Double.parseDouble(txtNumHoras.getText());
        double Valor_Hora = Double.parseDouble(txtValorHora.getText());
        double Sal_Bruto = (Horas_Trabajadas*Valor_Hora);
        
        txtSalBruto.setText(String.valueOf(Sal_Bruto));
        
    }//GEN-LAST:event_btnSalBrutoActionPerformed

