    private void btnSalNetoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSalNetoActionPerformed
        // TODO add your handling code here:
        
        double Sal_Bruto = Double.parseDouble(txtSalBruto.getText());
        double Porcentaje_Retencion = Double.parseDouble(txtRetencion.getText());
        double Sal_Neto = ((Sal_Bruto)-(Sal_Bruto*(Porcentaje_Retencion/100)));
        
        txtSalNeto.setText(String.valueOf(Sal_Neto));
    }//GEN-LAST:event_btnSalNetoActionPerformed

